$.extend({
    getUrlVars: function () {
        var vars = [], hash; var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&'); for (var i = 0; i < hashes.length; i++) { hash = hashes[i].split('='); vars.push(hash[0]); vars[hash[0]] = hash[1] }
        return vars
    }, getUrlVar: function (name) { return $.getUrlVars()[name] }
}); $('.pagination-previous').on('click', function () { $('.pagination-previous a').removeAttr('href'); previousNext(!0) }); $('.pagination-next').on('click', function () { $('.pagination-next a').removeAttr('href'); previousNext(!1) }); $('.pagination li>a').on('click', function () { $(this).removeAttr('href'); navigate($(this).text()) }); function previousNext(is_previous) {
    var currentPage = parseInt($('.pagination li.current').find('span').remove().end().text()); var new_page_num = currentPage + 1; if (is_previous == !0) { new_page_num = currentPage - 1 }
    navigate(new_page_num)
}
function navigate(new_page_num) {
    var parsedUrl = window.location.href; var queryString = location.search; var urlParams = new URLSearchParams(location.search); if (queryString == "") { parsedUrl += "?page=" + new_page_num }
    else {
        if (urlParams.has('page')) { parsedUrl = parsedUrl.split('?')[0]; urlParams.delete('page'); parsedUrl += "?" + urlParams + "&page=" + new_page_num }
        else { parsedUrl += "&page=" + new_page_num }
    }
    window.location.href = parsedUrl
}